function sendFloat(value, address) {
    let buf = Buffer.allocUnsafe(4);
    buf.writeFloatBE(value);
    let bytes = buf.toJSON()["data"];
    let reg1 = Buffer.from([bytes[2], bytes[3]]).readUInt16BE();
    let reg2 = Buffer.from([bytes[0], bytes[1]]).readUInt16BE();

    setTimeout(function() {
        node.send({
                "payload": {
                    "fc": 6,
                    "unitid": 253,
                    "address": address,
                    "value": reg1,
                    "quantity": 1
                }
        });
    }, 100);

    setTimeout(function() {
        node.send({
                "payload": {
                    "fc": 6,
                    "unitid": 253,
                    "address": address+1,
                    "value": reg2,
                    "quantity": 1
                }
        });
    }, 2000);
}

